#[derive(PartialEq, Clone, Debug, Serialize, Deserialize, Copy)]
pub enum Role {
    Admin,
    User,
}

#[derive(Serialize, Deserialize, PartialEq, Debug, Responder)]
pub enum Code {
    Success(()),
    Error(String),
}

pub fn error<'a, T>(msg: &'a str) -> Json<Response<T>> {
    Json(Response {
        code: Code::Error(msg.to_string()),
        total: None,
        data: None
    })
}